package com.zpower.restservice.demo.controller;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import java.util.HashMap;import java.util.Map;@RestControllerpublic class RestDemoController {    @RequestMapping("/helloworld")    public Map helloWorld() {        Map map = new HashMap<String, String>();        map.put("data", "Hello World");        return map;    }}